import React, { Component } from "react";
import api from "../api";

export default class MainPage extends Component {
  state = { items: [] };

  setItems = async () => {
    const items = await api.getItems();
    this.setState({ items });
  };
  componentDidMount = () => {
    this.setItems();
  };

  render() {
    return (
      <div>
        <h1>Welcome to my site</h1>
        <button onClick={() => api.deleteItem(3)}>Delete first item</button>
        <button onClick={api.getItems}>Console log Items</button>
        <button onClick={this.addItem}>Add mock item</button>
        <button onClick={this.editItem}>Edit item</button>
      </div>
    );
  }
}
