import React, { Component } from "react";
import api from "../api";

export default class EditPage extends Component {
  state = {
    itemName: "",
    itemDescription: "",
    itemCount: null,
    itemImage: "",
  };
  id = this.props.match.params.id;

  setItem = async (id) => {
    const item = await api.getItem(id);
    console.log(item);
    this.setState({
      itemName: item.itemname,
      itemDescription: item.description,
      itemCount: item.count,
      itemImage: item.image,
    });
  };

  componentDidMount = () => {
    this.setItem(this.id);
  };

  render() {
    return (
      <form className="ui form">
        <div className="field">
          <label>Product Name</label>
          <input type="text" name="product-name" placeholder="Product Name" value={this.state.itemName} />
        </div>
        <div className="field">
          <label>Description</label>
          <textarea name="description" id="" cols="30" rows="10" value={this.state.itemDescription}></textarea>
        </div>
        <div className="field">
          <label>Count</label>
          <input type="number" name="count" placeholder="Count" value={this.state.itemCount} />
        </div>
        <div className="field">
          <label>Image URL</label>
          <input type="text" name="image-url" placeholder="Image URL" />
        </div>
        <button className="ui button" type="submit">
          Submit
        </button>
      </form>
    );
  }
}
