import React, { Component } from "react";

export default class ItemCard extends Component {
  render() {
    const { image, itemname, description, count } = this.props.item;
    return (
      <div class="card">
        <div class="image">
          <img src={image} />
        </div>
        <div class="content">
          <div class="header">{itemname}</div>
          <div class="meta">
            <a>Friends</a>
          </div>
          <div class="description">{description}</div>
        </div>
        <div class="extra content">
          <span class="right floated">Count: {count}</span>
        </div>
      </div>
    );
  }
}
